con:config.script:
import groovy.json.JsonSlurper

def jsonResp = context.expand( '${GET Request - Valid ZipCode, Valid PlayDate (compared to OT ExternalAPI)#Response#$[\'data\']}' ) 
def jsonElements = new JsonSlurper().parseText(jsonResp)
def distanceArray = [];
def totalCourseTimesArray = []
int totalCourseTimes = 0
def totalDealTimesArray = []
int totalDealTimes = 0
int totalTeeTimesByCourse = 0

def TotalDealTimesResponse = context.expand( '${GET Request - Valid ZipCode, Valid PlayDate (compared to OT ExternalAPI)#Response#$[\'reference\'][0][\'value\']}' )
def TotalCourseTimesResponse = context.expand( '${GET Request - Valid ZipCode, Valid PlayDate (compared to OT ExternalAPI)#Response#$[\'reference\'][1][\'value\']}' )
def LimitResponse = context.expand( '${GET Request - Valid ZipCode, Valid PlayDate (compared to OT ExternalAPI)#Response#$[\'limit\']}' )

for ( value in jsonElements ) {
   distanceArray.add(value["distance"])
   int totalDealsAvailableByCourse = value["totalDealsAvailableByCourse"].toInteger()
   totalDealTimesArray.add(value["totalDealsAvailableByCourse"])
   totalDealTimes = totalDealTimes + totalDealsAvailableByCourse

   int totalCourseAvailableByCourse = value["totalTeeTimesByCourse"].toInteger()
   totalCourseTimesArray.add(value["totalTeeTimesByCourse"])
   totalCourseTimes = totalCourseTimes + totalCourseAvailableByCourse

   if(totalDealsAvailableByCourse + totalCourseAvailableByCourse == 1) {  	 
   	 //Assert: if total teetimes == 1, priceFrom should be equal with priceTo
   	 assert value["priceFrom"].toString() == value["priceTo"].toString()

     //Assert: if total teetimes == 1, firstTeeTime should be equal with lastTeeTime
   	 assert value["firstTeeTime"].toString() == value["lastTeeTime"].toString()

   	 log.info "I only have 1 totalTeeTimes!!!!"
   	 assert jsonElements.size() <= LimitResponse.toInteger()
   }

   //Assert: Distance should be a valid value(Non-Negative)
   assert  value["distance"].toInteger() > 0
}

log.info totalCourseTimesArray
log.info totalDealTimesArray

//****Assert: Courses are sorted by Distance (nearest -> farthest)****//
List.metaClass.isSorted = { -> delegate == delegate.sort( false ) }
log.info distanceArray
assert distanceArray.isSorted()
//****end****//

//Assert: Returning correct TotalDealTimes under reference
assert TotalDealTimesResponse.toInteger() == totalDealTimes

//Assert: Returning correct TotalCourseTimes under reference
assert TotalCourseTimesResponse.toInteger() == totalCourseTimes.toInteger() - totalDealTimes.toInteger()

//Assert: total of TotalCourseTimes & TotalDealTimes is equal to total of each course's totalTeeTimesByCourse
assert totalCourseTimes.toInteger() == TotalDealTimesResponse.toInteger() + TotalCourseTimesResponse.toInteger()

log.info totalCourseTimes.toString()
log.info totalDealTimes.toString()
log.info TotalCourseTimesResponse.toString()

con:testCase.con:setupScript:
def dateToday = new Date()
playDate = dateToday + 2 playDateMoreThanSevenDays = dateToday + 8 playDate1MonthFromToday = dateToday + 31 playDate14DaysFromToday = dateToday + 14
testRunner.testCase.setPropertyValue("playDate", playDate.format("MM-dd-yyyy")) testRunner.testCase.setPropertyValue("playDateMoreThanSevenDays", playDateMoreThanSevenDays.format("MM-dd-yyyy")) testRunner.testCase.setPropertyValue("dateToday", dateToday.format("MM-dd-yyyy")) testRunner.testCase.setPropertyValue("playDate1MonthFromToday", playDate1MonthFromToday.format("MM-dd-yyyy")) testRunner.testCase.setPropertyValue("playDate14DaysFromToday", playDate14DaysFromToday.format("MM-dd-yyyy"))
