con:config.script:
def count = testRunner.testCase.testSuite.project.getPropertyValue("count")
def testDate = new Date()
int countValue = count.toInteger()
def TestDate = testDate + countValue

log.info countValue
log.info TestDate

if(countValue > 14) {
	testRunner.gotoStep(testRunner.testCase.getTestStepIndexByName('Property Transfer: Move Tee Time Details'))
     testRunner.testCase.testSuite.project.setPropertyValue("count", "0")
     testRunner.fail("Property Transfer: Move Tee Time Details")
	return null
}

def ResponseData = context.expand( '${Test Request: Get tee times by Course#ResponseAsXml#declare namespace ns1=\'https://dev-api.opentee.com/TeeOff/v1/Search/GetTeeTimesByCourse\'; //ns1:Response[1]/ns1:Data[1]}' )

log.info ResponseData
log.info ResponseData.size()

if(ResponseData.size() != 88) {
 testRunner.gotoStep(testRunner.testCase.getTestStepIndexByName('Property Transfer: Move Tee Time Details'))
 testRunner.testCase.testSuite.project.setPropertyValue("count", "0")
}
else {
 testRunner.gotoStep(testRunner.testCase.getTestStepIndexByName('Test Request: Get tee times by Course'))
 testRunner.testCase.testSuite.project.setPropertyValue("TestDate", TestDate.format("MM/dd/yyyy"))
 testRunner.testCase.testSuite.project.setPropertyValue("count", (countValue + 1).toString())
 log.info countValue
}

con:testCase.con:setupScript:
def dateToday = new Date()
playDate = dateToday + 2 playDateMoreThanSevenDays = dateToday + 8
testRunner.testCase.testSuite.project.setPropertyValue("playDate", playDate.format("MM/dd/yyyy")) testRunner.testCase.testSuite.project.setPropertyValue("playDateMoreThanSevenDays", playDateMoreThanSevenDays.format("MM/dd/yyyy")) testRunner.testCase.testSuite.project.setPropertyValue("dateToday", dateToday.format("MM/dd/yyyy"))
