import groovy.json.JsonSlurper

def response = context.expand( '${Test Request: GetTeeTimesByCourse#Response#$[\'Data\'][0][\'TeeTimes\'][*][\'TeeTimePurchase\']}' )
def jsonTeeTimesElements = new JsonSlurper().parseText(response)

def dataResponse = context.expand( '${Test Request: GetCourseTeeTimesSummaries (compared to other method calls)#Response#$[\'Data\']}' )
def jsonDataElements = new JsonSlurper().parseText(dataResponse)

def TeeTimesResponse = context.expand( '${Test Request: GetTeeTimesByCourse#Response#$[\'Data\'][0][\'TeeTimes\']}' )
def TeeTimesElements = new JsonSlurper().parseText(TeeTimesResponse)
def TeeTimesArray = []
for ( value in TeeTimesElements ) {
	TeeTimesArray.add(value["CourseTeeTime"])
}

log.info TeeTimesArray

//***GetTeeTimesByCourse***
def GetTeeTimeByCourseMinTeeTime = TeeTimesArray.first()
def dtmin = Date.parse("yyyy-MM-dd'T'HH:mm:ss",GetTeeTimeByCourseMinTeeTime)
def minTeeTime = dtmin.format("h:mm:ss")
log.info minTeeTime

def GetTeeTimeByCourseMaxTeeTime = TeeTimesArray.last()
def dtmax = Date.parse("yyyy-MM-dd'T'HH:mm:ss",GetTeeTimeByCourseMaxTeeTime)
def maxTeeTime = dtmax.format("h:mm:ss")
log.info maxTeeTime
//***GetTeeTimesByCourse***

//***GetCourseTeeTimesSummaries***
def MinTeeTimeResponse = context.expand( '${Test Request: GetCourseTeeTimesSummaries (compared to other method calls)#Response#$[\'Data\'][0][\'MinTeeTime\']}' )
def GCMin = new Date(MinTeeTimeResponse).format("h:mm:ss")
log.info GCMin

def MaxTeeTimeResponse = context.expand( '${Test Request: GetCourseTeeTimesSummaries (compared to other method calls)#Response#$[\'Data\'][0][\'MaxTeeTime\']}' )
def GCMax = new Date(MaxTeeTimeResponse).format("h:mm:ss")
log.info GCMax
//***GetCourseTeeTimesSummaries***

//Assert: MinTeeTime is same with GetTeeTimesByCourse
assert minTeeTime ==  GCMin

//Assert: MaxTeeTime is same with GetTeeTimesByCourse
assert maxTeeTime == GCMax

def Prices = [];
def FromPrice = context.expand( '${Test Request: GetCourseTeeTimesSummaries (compared to other method calls)#Response#$[\'Data\'][0][\'PriceFrom\']}' )
def ToPrice = context.expand( '${Test Request: GetCourseTeeTimesSummaries (compared to other method calls)#Response#$[\'Data\'][0][\'PriceTo\']}' )

def playDate = context.expand( '${#Project#playDate}' )

//check if correct Playdate
for ( value in jsonDataElements ) {
   def fromTimedate = Date.parse("MM/dd/yyyy", value["MinTeeTime"].take(10))
   def toTimedate = Date.parse("MM/dd/yyyy", value["MaxTeeTime"].take(10))
   String fromformattedDate = fromTimedate.format( 'MM/dd/yyyy' )
   String toformattedDate = toTimedate.format( 'MM/dd/yyyy' )

	log.info fromformattedDate
	log.info toformattedDate

   //Assert if correct playDate
   assert fromformattedDate == playDate
   assert toformattedDate == playDate
}

for ( value in jsonTeeTimesElements ) {
	Prices.add(value["Price"].toInteger())
}

log.info Prices.min()
log.info Prices.max()

log.info Prices

//GetCourseTeeTimesSummaries FromPrice and ToPrice
log.info Integer.parseInt(String.valueOf(FromPrice).split("\\.")[0]);
log.info Integer.parseInt(String.valueOf(ToPrice).split("\\.")[0]);

//Assert if FromPrice is same with GetTeeTimesByCourse
assert Prices.min() == Integer.parseInt(String.valueOf(FromPrice).split("\\.")[0]);

//Assert if ToPrice is same with GetTeeTimesByCourse
assert Prices.max() == Integer.parseInt(String.valueOf(ToPrice).split("\\.")[0]);

import groovy.json.JsonSlurper

def response = context.expand( '${Test Request: GetCourseTeeTimesSummaries (Filter: NumberOfHoles =  9)#Response#$[\'Data\']}' )
def jsonTeeTimesElements = new JsonSlurper().parseText(response)

def playDate = context.expand( '${#Project#playDate}' )
def fromTimeDate = []
def toTimeDate = []
def numberOfHolesArray = []

def responseHoles = context.expand( '${Test Request: GetTeeTimesByCourse (Filter: NumberOfHoles = 9)#Response#$[\'Data\'][*][\'TeeTimes\'][*][\'TeeTimeInformation\']}' )
def jsonHolesElements = new JsonSlurper().parseText(responseHoles)

//check if correct Playdate
for ( value in jsonTeeTimesElements ) {
   def fromTimedate = Date.parse("MM/dd/yyyy", value["MinTeeTime"].take(10))
   def toTimedate = Date.parse("MM/dd/yyyy", value["MaxTeeTime"].take(10))
   String fromformattedDate = fromTimedate.format( 'MM/dd/yyyy' )
   String toformattedDate = toTimedate.format( 'MM/dd/yyyy' )
   fromTimeDate.add(fromformattedDate)
   toTimeDate.add(toformattedDate)

   //Assert if correct playDate
   assert fromformattedDate == playDate
}

for ( value in jsonHolesElements ) {
  numberOfHolesArray.add(value["NumberOfHoles"])
}

//check if course really contains 9 holes
assert numberOfHolesArray.toString().contains("9")

log.info numberOfHolesArray
log.info fromTimeDate
log.info toTimeDate

