con:config.script:
import groovy.json.JsonSlurper



//def jsonResp = context.expand( '${Test Request: GetTeeTimesByCourse (Setting minDistance/maxDistance))#Response#$[\'Data\']}' )



def response = context.expand( '${Test Request: GetTeeTimesDeals (Setting Min/Max Distance)#Response#$[\'Data\'][\'TeeTimeDealsResult\']}' )
def jsonTeeTimesElements = new JsonSlurper().parseText(response)

def jsonResp = context.expand( '${Test Request: GetTeeTimesDeals (Setting Min/Max Distance)#Response#$[\'Data\'][\'TeeTimeDealsResult\'][*][\'CourseInformation\']}' )
def jsonElements = new JsonSlurper().parseText(jsonResp)
def distanceArray = [];
def requestmaxDistance = context.expand( '${Test Request: GetTeeTimesDeals (Setting Min/Max Distance)#request.maxDistance}' )
def requestminDistance = context.expand( '${Test Request: GetTeeTimesDeals (Setting Min/Max Distance)#request.minDistance}' )

def playDate = context.expand( '${#Project#playDate}' )
def teeTimes = []

//def BeginTimeEndTimeResponse = context.expand( '${Test Request: GetTeeTimesByCourse (Setting beginTime/endTime))#Response#$[\'Data\'][0][\'TeeTimes\']}' )
//def jsonBETeeTimesElements = new JsonSlurper().parseText(BeginTimeEndTimeResponse)

//check if correct Playdate
for ( value in jsonTeeTimesElements ) {
   def newerdate = Date.parse("yyyy-MM-dd", value["CourseTeeTime"].take(10))
   String formattedDate = newerdate.format( 'MM/dd/yyyy' )
   teeTimes.add(formattedDate)

   //Assert if correct playDate
   assert formattedDate == playDate
}

//check if results are within the filter range (MIN-MAX)
for ( value in jsonElements ) {
	def ivalue = value["Distance"]
   distanceArray.add(value["Distance"])
   assert (ivalue >= requestminDistance.toInteger()) && (ivalue <= requestmaxDistance.toInteger())
}

log.info teeTimes
log.info distanceArray

con:testCase.con:setupScript:
def dateToday = new Date()
playDate = dateToday + 2 playDateMoreThanSevenDays = dateToday + 8
testRunner.testCase.testSuite.project.setPropertyValue("playDate", playDate.format("MM/dd/yyyy")) testRunner.testCase.testSuite.project.setPropertyValue("playDateMoreThanSevenDays", playDateMoreThanSevenDays.format("MM/dd/yyyy")) testRunner.testCase.testSuite.project.setPropertyValue("dateToday", dateToday.format("MM/dd/yyyy"))
